import React, { Component } from "react";
import axios from "axios";
import "./displaySaleLogs.css";

class DisplaySaleLogs extends Component {
  state = {
    data: null,
  };
  refreshData() {
    axios
      .get("http://localhost:8000/sales/")
      .then((response) => {
        // console.log(response.data);
        // console.log(response);
        this.setState({ data: response.data });
        console.log(this.state.data, "Hello world");
      })
      .catch((error) => {
        console.log(error);
      });
  }
  componentDidMount = () => {
    this.refreshData();
  };
  componentWillMount = () => {
    this.refreshData();
  };
  renderData = () => {
      return this.state.data.map()
  };

  render() {
    return (
      <React.Fragment>
        <span className="title">Sale Logs</span>
        <table>
          <thead>
            <tr>
              <th>Sl. NO</th>
              <th>Date</th>
              <th>Stone Type</th>
              <th>Vehicle No</th>
              <th>
                Quantitiy <br />
                (in Brass)
              </th>
              <th>Delivery Executive</th>
              <th>Diesel Spent</th>
            </tr>
          </thead>
          <tbody>{this.renderData()}</tbody>
        </table>
      </React.Fragment>
    );
  }
}

export default DisplaySaleLogs;
