import React, { Component } from "react";
import axios from "axios";
import MainTemplate from "./mainTemplate.jsx";
import "./displayWorkers.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faFemaile
} from "@fortawesome/free-solid-svg-icons";

class DisplayWorkers extends Component {
  state = {
    data: [],
  };
  refreshData = () => {
    axios
      .get("http://localhost:8000/worker/")
      .then((response) => {
        this.setState({ data: response.data });
        console.log("data received");
        console.log(response.data);
      })
      .catch((error) => {
        console.log(error.data);
      });
  };
  componentWillMount = () => {
    console.log("did mount");
    this.refreshData();
  };
  componentDidMount = () => {
    this.refreshData();
  };
  getIcon = (mobile) => {
    for (let i = 0; i < this.state.data.length; ++i) {
      if (this.state.data.mobile === mobile) {
        if (this.state.data.gender === "f") return faFemale;
        else return faMale;
      }
    }
  };
  renderData = () => {
    return (
      <div className="display-body">
        {this.state.data.map((item) => {
          return (
            <div key={item.id} className="cardw">
              <img
                src={"http://localhost:8000" + item.photo}
                alt={item.photo}
                height="100px"
                width="150px"
              />
              <div className="cardw-body">
                <span className="cardw-title">
                  <FontAwesomeIcon
                    className="fas"
                    icon={this.getIcon(item.mobile)}
                  />
                  {item.name}
                </span>
                <p className="cardw-text">
                  <ul>
                    <li>{item.title}</li>
                    <li>{item.mobile}</li>
                    <li>{item.salary}</li>
                    <li>{item.aadhar_number}</li>
                    <li>{item.date_of_joining}</li>
                  </ul>
                </p>
                <button className="btnw">Delete</button>
              </div>
            </div>
          );
        })}
      </div>
    );
  };
  render() {
    return (
      <MainTemplate>
        <span className="page-title">Employees</span>
        {this.renderData()}
      </MainTemplate>
    );
  }
}

export default DisplayWorkers;
