import React, { Component } from "react";
import axios from "axios";
import MainTemplate from "./mainTemplate.jsx";
import "./displayWorkers.css";

class DisplayWorkers extends Component {
  state = {
    data: [],
  };
  refreshData = () => {
    axios
      .get("http://localhost:8000/worker/")
      .then((response) => {
        this.setState({ data: response.data });
        console.log("data received");
        console.log(response.data);
      })
      .catch((error) => {
        console.log(error.data);
      });
  };
  componentWillMount = () => {
    console.log("did mount");
    this.refreshData();
  };
  componentDidMount = () => {
    this.refreshData();
  };
  renderData = () => {
    return (
      <div className="display-body">
        {this.state.data.map((item) => {
          return (
            <div key={item.id} className="card">
              <img
                src={"http://localhost:8000" + item.photo}
                alt={item.photo}
              />
              <div className="card-body">
                <h5 className="card-title">{item.name}</h5>
                <p className="card-text">{item.mobile}</p>
                <a href="#" className="btn btn-primary">
                  Delete
                </a>
              </div>
            </div>
          );
        })}
      </div>
    );
  };
  render() {
    return <MainTemplate>{this.renderData()}</MainTemplate>;
  }
}

export default DisplayWorkers;
