import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.css";
import "./login.css";
import LoginForm from "./loginForm";
import Logo from "./logo2.JPG";
class Login extends Component {
  state = {
    username: "",
    password: "",
  };
  render() {
    return (
      <div className="row no-gutters">
        <div className="col left-side d-flex justify-content-center align-items-center no-gutters">
          <img src={Logo} alt="Logo" />
        </div>
        <div className="col right-side d-flex justify-content-center align-items-center no-gutters">
          <fieldset
            className="justify-content-center align-center"
            style={{ textAlign: "center" }}
          >
            <legend> Login</legend>
            <input
              type="text"
              value={this.state.username}
              placeholder="Username"
            />
            <br />
            <br />
            <input
              type="password"
              value={this.state.password}
              placeholder="Password"
            />{" "}
            <br />
            <br />
            <input type="submit" />
          </fieldset>
        </div>
      </div>
    );
  }
}

export default Login;
