import React, { Component } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.css";

class DisplayWorkers extends Component {
  state = {
    data: [],
  };
  refreshData = () => {
    axios
      .get("http://localhost:8000/worker/")
      .then((response) => {
        this.setState({ data: response.data });
        console.log("data received");
        console.log(response.data);
      })
      .catch((error) => {
        console.log(error.data);
      });
  };
  componentWillMount = () => {
    console.log("did mount");
    this.refreshData();
  };
  componentDidMount = () => {
    this.refreshData();
  };
  renderData = () => {
    return this.state.data.map((item) => {
      return (
        <div key={item.id} className="card" style={{ width: "18em" }}>
          <img src={"http://localhost:8000" + item.photo} alt={item.photo} />
          <div className="card-body">
              <h5 className="card-title">{item.name}</h5>
      <p className="card-text">{item.mobile}</p>
          </div>
        </div>
      );
    });
  };
  render() {
    return <React.Fragment>{this.renderData()}</React.Fragment>;
  }
}

export default DisplayWorkers;
