import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.css";
import "./login.css";
import Logo from "./logo2.JPG";
class Login extends Component {
  // state = {  }
  render() {
    return (
      <div className="row no-gutters">
        <div className="col d-flex justify-content-center align-items-center no-gutters">
          <div className="left-side d-flex justify-content-center align-items-center no-gutters">
            <span className={{ backgroundIamge: "url(" + { Logo } + ")" }}>
              Left side
            </span>
          </div>
        </div>
        <div className="col d-flex justify-content-center align-items-center no-gutters">
          <div className="right-side d-flex justify-content-center align-items-center no-gutters">
            Right side
          </div>
        </div>
      </div>
    );
  }
}

export default Login;
