import React, { Component } from "react";
import MainTemplate from "./mainTemplate";
import "./addWorker.css";
import Axios from "axios";
class AddWorker extends Component {
  state = {
    name: "",
    gender: "f",
    job_title: "",
    date: "",
    mobile: "",
    aadhar: "",
    salary: "",
    photo: null,
  };
  handleSubmit = (event) => {
    event.preventDefault();
    console.log(this.state);
    let formData = new FormData();
    for (let property in this.setState) {
      formData.append(property, this.setState[property]);
    }
    Axios
  };
  handleChange = (event) => {
    console.log(event);
    let obj = { [event.target.name]: event.target.value };
    this.setState(obj);
    console.log(obj);
    console.log(event.target.name, "=>", event.target.value);
  };
  handleFileUpload = (event) => {
    console.log("Done");
    this.setState({ photo: event.target.files[0] });
  };
  render() {
    return (
      <MainTemplate>
        <div className="form-wrapper">
          <form onSubmit={this.handleSubmit}>
            <span className="form-title">--Employee Details--</span>
            <br />
            <br />
            <table>
              <tbody>
                <tr>
                  <td>Name:</td>
                  <td>
                    <input
                      className="not-photo"
                      type="text"
                      name="name"
                      value={this.state.name}
                      onChange={this.handleChange}
                    />
                  </td>
                </tr>
                <tr>
                  <td>Gender:</td>
                  <td>
                    <select
                      name="gender"
                      value={this.state.gender}
                      onChange={this.handleChange}
                      className="not-photo"
                    >
                      <option value="f">Female</option>
                      <option value="m">Male</option>
                      <option value="o">Other</option>
                    </select>
                  </td>
                </tr>

                <tr>
                  <td>Job Title:</td>
                  <td>
                    <input
                      className="not-photo"
                      type="text"
                      name="job_title"
                      value={this.state.job_title}
                      onChange={this.handleChange}
                    />
                  </td>
                </tr>

                <tr>
                  <td>Join Date:</td>
                  <td>
                    <input
                      className="not-photo"
                      type="date"
                      name="date"
                      value={this.state.date}
                      onChange={this.handleChange}
                    />
                  </td>
                </tr>
                <tr>
                  <td>Mobile:</td>
                  <td>
                    <input
                      className="not-photo"
                      type="text"
                      name="mobile"
                      value={this.state.mobile}
                      onChange={this.handleChange}
                    />
                  </td>
                </tr>
                <tr>
                  <td>Aadhaar Number:</td>
                  <td>
                    <input
                      className="not-photo"
                      type="text"
                      name="aadhar"
                      value={this.state.aadhar}
                      onChange={this.handleChange}
                    />
                  </td>
                </tr>
                <tr>
                  <td>Salary:</td>
                  <td>
                    <input
                      className="not-photo"
                      type="text"
                      name="salary"
                      value={this.state.salary}
                      onChange={this.handleChange}
                    />
                  </td>
                </tr>
                <tr>
                  <td>Photo: </td>
                  <td>
                    <input
                      className="photo"
                      type="file"
                      name="photo"
                      onChange={this.handleFileUpload}
                    />
                  </td>
                </tr>
                <tr>
                  <td></td>
                  <td>
                    <input
                      type="submit"
                      value="Add Employee"
                      className="submit-button"
                    />
                  </td>
                </tr>
              </tbody>
            </table>
          </form>
        </div>
      </MainTemplate>
    );
  }
}

export default AddWorker;

