import React, { Component } from "react";
import axios from "axios";
class SaleLogForm extends Component {
  state = {
    data: {
      vehicle: "",
      stonetype: "",
      quantity: "",
      delivery_worker: "",
      diesel_stock_name: "",
      desiel_spent: "",
      sale_bill: "",
    },
    vehicles: [],
    stonetypes: [],
    delivery_workers: [],
    diesel_stock_name: [],
    sale_bills: [],
    errorMessage: "",
  };
  componentWillMount=()=>{
      this.getsale_bill()
  }
  getsale_bill = () => {
    axios
      .get("https://localhost:8000/bills/")
      .then((response) => {
        this.setState({ sale_bills: response.data });
      })
      .catch((error) => {
        console.log(error.response);
      });
  };
  getdiesel_stock_name = () => {
    axios
      .get("https://localhost:8000/diesel_stocks/")
      .then((response) => {
        this.setState({ diesel_stock_name: response.data });
      })
      .catch((error) => {
        console.log(error.response);
      });
  };

  getErrorMessageStyle = () => {
    let styleObject = { display: "none" };
    if (this.state.errorMessage != "") {
      styleObject = {
        width: "300px",
        color: "red",
        fontSize: "12px",
        lineHeight: "15px",
        border: "solid 0.5px red",
      };
    }
    return styleObject;
  };
  getVehicles = () => {
    axios
      .get("https://localhost:8000/sales/")
      .then((response) => {
        this.setState({ vehicles: response.data });
      })
      .catch((error) => {
        console.log(error.response);
      });
  };
  getStoneTypes = () => {
    axios
      .get("https://localhost:8000/stone_types/")
      .then((response) => {
        this.setState({ stonetypes: response.data });
      })
      .catch((error) => {
        console.log(error.response);
      });
  };
  getdelivery_workers = () => {
    axios
      .get("https://localhost:8000/worker/")
      .then((response) => {
        this.setState({ delivery_workers: response.data });
      })
      .catch((error) => {
        console.log(error.response);
      });
  };

  render() {
    return (
      <div className="form-wrapper">
        <form onSubmit={this.handleSubmit} encType="multipart/form-data">
          <span className="form-title">--Employee Details--</span>
          <br />
          <br />
          <table>
            <tbody>
              <tr>
                <td>Name:</td>
                <td>
                  <input
                    className="not-photo"
                    type="text"
                    name="name"
                    value={this.state.data.name}
                    onChange={this.handleChange}
                    required
                  />
                </td>
              </tr>
              <tr>
                <td>Gender:</td>
                <td>
                  <select
                    name="gender"
                    value={this.state.data.gender}
                    onChange={this.handleChange}
                    required
                    className="not-photo"
                  >
                    <option value="f">Female</option>
                    <option value="m">Male</option>
                    <option value="o">Other</option>
                  </select>
                </td>
              </tr>

              <tr>
                <td>Job Title:</td>
                <td>
                  <input
                    className="not-photo"
                    type="text"
                    name="title"
                    value={this.state.data.title}
                    onChange={this.handleChange}
                    required
                  />
                </td>
              </tr>

              <tr>
                <td>Join Date:</td>
                <td>
                  <input
                    className="not-photo"
                    type="date"
                    name="date_of_joining"
                    value={this.state.data.date_of_joining}
                    onChange={this.handleChange}
                    required
                  />
                </td>
              </tr>
              <tr>
                <td>Mobile:</td>
                <td>
                  <input
                    className="not-photo"
                    type="text"
                    name="mobile"
                    value={this.state.data.mobile}
                    onChange={this.handleChange}
                    required
                  />
                </td>
              </tr>
              <tr>
                <td>Aadhaar Number:</td>
                <td>
                  <input
                    className="not-photo"
                    type="text"
                    name="aadhar_number"
                    value={this.state.data.aadhar_number}
                    onChange={this.handleChange}
                    required
                  />
                </td>
              </tr>
              <tr>
                <td>Salary:</td>
                <td>
                  <input
                    className="not-photo"
                    type="text"
                    name="salary"
                    value={this.state.data.salary}
                    onChange={this.handleChange}
                    required
                  />
                </td>
              </tr>
              <tr>
                <td>Photo: </td>
                <td>
                  <input
                    className="photo"
                    type="file"
                    name="photo"
                    onChange={this.handleFileUpload}
                  />
                </td>
              </tr>
              <tr>
                <td>
                  <input
                    type="submit"
                    value="Add Employee"
                    className="submit-button"
                  />
                </td>
                <td>
                  <span
                    className="error-message"
                    style={this.getErrorMessageStyle()}
                  >
                    {this.state.errorMessage}
                  </span>
                </td>
              </tr>
            </tbody>
          </table>
        </form>
      </div>
    );
  }
}

export default SaleLogForm;
